default.stylesheet:
QMainWindow, QWidget {
    background-color: #f0f0f0;
    color: #333333;
}
QPushButton {
    background-color: #e0e0e0;
    border: 1px solid #bbbbbb;
    padding: 6px;
    border-radius: 4px;
}
QPushButton:hover {
    background-color: #d0d0d0;
}
QGroupBox {
    font-weight: bold;
    border: 2px solid #bbbbbb;
    border-radius: 6px;
    margin-top: 1.5ex;
    padding-top: 10px;
}
QGroupBox::title {
    subcontrol-origin: margin;
    left: 10px;
    padding: 0 5px;
}
QComboBox {
    background-color: white;
    border: 1px solid #bbbbbb;
    padding: 4px;
    border-radius: 4px;
}
QComboBox::drop-down {
    border-left: 1px solid #bbbbbb;
}
QSlider::groove:horizontal {
    border: 1px solid #bbbbbb;
    height: 8px;
    background: #e0e0e0;
    margin: 2px 0;
    border-radius: 4px;
}
QSlider::handle:horizontal {
    background: #05B8CC;
    border: 1px solid #999999;
    width: 18px;
    margin: -2px 0;
    border-radius: 4px;
}
QLabel {
    color: #333333;
}
QStatusBar, QLabel#status_bar {
    background-color: #e0e0e0;
    padding: 4px;
    border-radius: 4px;
}

default.volume_bar_style:
QProgressBar {
    border: 2px solid #888888;
    border-radius: 5px;
    text-align: center;
    background-color: #f0f0f0;
}
QProgressBar::chunk {
    background-color: #05B8CC;
    width: 10px;
}

